<style lang="scss" scoped>
.footer {
  position: relative;
  $background-color: #061a1a;
  background: #061a1a;
  width: 100%;
  margin-top: 24rem;
  padding-top: 15rem;
  color: white;

  &__container {
    display: flex;
    justify-content: space-between;
    flex-direction: row;
  }

  &::before {
    --width: 110vw;
    content: '';
    position: absolute;
    background-color: $background-color;
    height: 100%;
    max-height: 92rem;
    width: var(--width);
    top: 0;
    left: calc((var(--width) / -2) + 50%);
    clip-path: ellipse(50% 14% at 50% 15%);
    transform: translateY(-9.5%);
    z-index: 2;

    // @include respond('desktop') {
    //   // --width: 120vw;
    // }

    // @include respond('tablet') {
    //   // --width: 140vw;
    // }

    // @include respond('mobile') {
    //   // --width: 165vw;
    // }
  }

  &::after {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    width: 100vw;
    left: calc(-50vw + 50%);
    height: 500px;
    margin: 0 auto;
    background-color: $background-color;
  }

  > * {
    position: relative;
    z-index: 2;
  }

  &__menu {
    display: flex;
    justify-content: space-between;

    &-column {
      display: flex;
      flex-direction: column;
      margin-left: 8rem;
    }

    &-title {
      font-size: 1.8rem;
      margin-bottom: 1rem;
      font-weight: 500;
    }

    a {
      font-size: 1.6rem;
      color: white;
      text-decoration: none;

      &:not(:last-child) {
        margin-bottom: 1rem;
      }
    }
  }

  &__social {
    &-links {
      position: relative;
      padding-top: 15rem;
      width: 100%;
      height: 50rem;
      width: 100vw;
      left: calc(-50vw + 50%);
      background-color: $background-color;
    }

    &-instagram {
      position: absolute;
      left: 10%;
    }

    &-twitter {
      position: absolute;
      left: 40%;
      top: 50%;
    }

    &-youtube {
      position: absolute;
      left: 70%;
    }
  }

  &__copyright {
    display: flex;
    align-items: center;
    justify-content: space-between;

    @include screen('med-small') {
      width: 100%;

      svg {
        transform: scale(0.7);
      }
    }

    span {
      font-weight: 500;
      color: #828282;
      margin-right: 5.2rem;

      @include screen('med') {
        margin-right: 3rem;
      }
    }
  }
}
</style>
